import React from "react";
import {
  BrowserRouter as Router,
  Redirect,
  Route,
  Link
} from "react-router-dom";

export default class Article extends React.Component {
  render() {
    return (
      <React.Fragment>
        <h2>          
          <Link to={"/articles/"+this.props.article.id}>{this.props.article.title} by {this.props.author.name}</Link>
        </h2>
        <p>{this.props.article.content}</p>
        <div className='delete-button' onClick={() => { if (window.confirm('Are you sure you wish to delete this item?')) this.onCancel(this.props.article.id) } } />
        <hr />
      </React.Fragment>
    );
  }
}
