import React from 'react'

export default class Register extends React.Component{

    constructor(props) {
        super(props);

        this.state = {
            user: {
                name: '',
                email: '',
                password: ''
            },
            submitted: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    andleChange(event) {
        const { name, value } = event.target;
        const { user } = this.state;
        this.setState({
            user: {
                ...user,
                [name]: value
            }
        });
    }

    handleSubmit(event) {
        event.preventDefault();

        this.setState({ submitted: true });
        const { user } = this.state;
        const { dispatch } = this.props;
        if (user.firstName && user.lastName && user.username && user.password) {
            dispatch(userActions.register(user));
        }
    }

    render(){
        return(
            <React.Fragment>
            <h1>Register Here!</h1>
            <div>
                <form>
                <div>
                <label style={{ margin:'10px'}}>Name</label>
                <input></input>
            </div>
            <div>
            <label style={{ margin:'10px'}}>Email</label>
                <input type="email"></input>
            </div>
            <div>
            <label style={{ margin:'10px'}}>Password</label>
                <input type="password"></input>
            </div>
            <div>
            <label style={{ margin:'10px'}}>Confirm Password</label>
                <input type="password"></input>
            </div>
            <div>
            <button type="submit" style={{ margin:'10px'}}>Register</button>
            </div>
                </form>
            </div>

        </React.Fragment>
        )
    }
}
