import React from 'react'

export default class Register extends React.Component{

    constructor() {
        super();
        this.state = {
          name: '',
          email: '',
          password: ''
        };

      }

      handleSubmit(e) {
        e.preventDefault();
        this.props.history.push('/login');
      }

    render(){
        return(
            <React.Fragment>
            <h1>Register Here!</h1>
            <div>
            <form onSubmit={this.handleSubmit.bind(this)}>
            <div>
            <label style={{ margin:'10px'}}>Name</label>
            <input id="name"
                  type="text"
                  name="name"
                  value={this.state.name}
                  onChange={this.onNameChange}></input>
            </div>
            <div>
            <label style={{ margin:'10px'}}>Email</label>
                <input type="email"></input>
            </div>
            <div>
            <label style={{ margin:'10px'}}>Password</label>
                <input type="password"></input>
            </div>
            <div>
            <label style={{ margin:'10px'}}>Confirm Password</label>
                <input type="password"></input>
            </div>
            <div>
            <button type="submit" style={{ margin:'10px'}}>Register</button>
            </div>
            </form>
            </div>

        </React.Fragment>
        )
    }
}
